package self.practice;

public class ForLoopOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=2;i<=15;i++) {

		if(i%2==0) {
			System.out.println("The number " +i+ " is Even");
		}
		else	{
			System.out.println("The number "+i+ " is Odd");
					}
		}
	}

}
